sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_CalculateParcel(vParcel, vExpected)
	{
		function TestCase_CalculateParcel_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				test.Log('vExpected is false');
				return (vExpected == vResult);
			}
			var grParcel = GetParcelRecord(vInput['vParcel']);
			test.Log('Reloading Parcel:');
			test.Log(grParcel);
			var fTotalVolume = x_snc_devtools.GetFloatValue(x_snc_devtools.GetValue(vExpected,'total_volume'));
			if (fTotalVolume != x_snc_devtools.GetFloatValue(grParcel.total_volume))
			{
				return false;
			}
			var nTotalWeight = x_snc_devtools.GetIntegerValue(x_snc_devtools.GetValue(vExpected,'total_weight'));
			if (nTotalWeight != x_snc_devtools.GetIntegerValue(grParcel.total_weight))
			{
				return false;
			}
			var nParts = x_snc_devtools.GetIntegerValue(x_snc_devtools.GetValue(vExpected,'parts'));
			if (nParts != x_snc_devtools.GetIntegerValue(grParcel.parts))
			{
				return false;
			}			
			return true;
		}

		test.TestCase(
			'Function CalculateParcel()',
			CalculateParcel,
			{
				'vParcel': vParcel
			},
			vExpected,
			TestCase_CalculateParcel_CheckResult);
	}

	TestCase_CalculateParcel(null,false);

	var grParcel = new GlideRecord('x_snc_parcel_parcel');
	grParcel.initialize();
	grParcel.insert();
	TestCase_CalculateParcel(grParcel,
		{
			total_volume: 0,
			total_weight: 0
		});



	var grParcelPart = new GlideRecord('x_snc_parcel_part');
	grParcelPart.initialize();
	grParcelPart.length = 100;
	grParcelPart.height = 100;
	grParcelPart.width = 100;
	grParcelPart.parcel = grParcel.sys_id;
	grParcelPart.weight = 100;
	grParcelPart.insert();
	TestCase_CalculateParcel(grParcel,
		{
			total_volume: 1,
			total_weight: 100,
			parts: 1
		});
	
	var grParcelPart = new GlideRecord('x_snc_parcel_part');
	grParcelPart.initialize();
	grParcelPart.length = 50;
	grParcelPart.height = 50;
	grParcelPart.width = 50;
	grParcelPart.parcel = grParcel.sys_id;
	grParcelPart.weight = 50;
	grParcelPart.insert();
	TestCase_CalculateParcel(grParcel,
		{
			total_volume: 1.125,
			total_weight: 150,
			parts: 2
		});

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
