sys_script_include.script:
function GetParcelRecord(vParcel)
{
	var GetStringValue = x_snc_devtools.GetStringValue;
	var IsValidSysId = x_snc_devtools.IsValidSysId;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetRecord = x_snc_devtools.GetRecord;

	var grParcel = GetRecord('x_snc_parcel_parcel',vParcel);
	if (IsValidRecord(grParcel) == true)
	{
		return grParcel;
	}

	var strParcel = GetStringValue(vParcel);

	strParcel = MakeValidParcelNumber(strParcel);

	var grRecord = new GlideRecord('x_snc_parcel_parcel');
	grRecord.addQuery('number','=',strParcel);
	grRecord.query();
	if (grRecord.next())
	{
		return grRecord;
	}
	return false;
}
