sys_script_include.script:
function DevToolsGetFormatConfig(grRecord)
{
	var strTable = grRecord.getRecordClassName();

	if (strTable == 'x_snc_parcel_parcel')
	{
		var formatconfig = {
			total_weight: {
				decimals: 0,
				postfix: ' kg'
			},
			total_volume: {
				decimals: 2,
				postfix: ' m³'
			}
		};
		return formatconfig;
	}	

	if (strTable == 'x_snc_parcel_part')
	{
		var formatconfig = {
			width: {
				decimals: 0,
				postfix: ' cm'
			},
			height: {
				decimals: 0,
				postfix: ' cm'
			},
			length: {
				decimals: 0,
				postfix: ' cm'
			},
			weight: {
				decimals: 0,
				postfix: ' kg'
			},
			volume: {
				decimals: 2,
				postfix: ' m³'
			}
		};
		return formatconfig;
	}

	return false;
}
